import React, { useState, useEffect } from 'react';

const EditUserForm = props => {
    const [product, setProduct] = useState(props.currentUser);

   const handleInputChange = e =>{
   const {nome, descricao, modelo, marca, tamanho, valor, quantidade, value} = e.target;
    setProduct({
        ...product,
        [nome]: value,
        [descricao]: value,
        [modelo]: value,
        [marca]: value,
        [tamanho]: value,
        [valor]: value,
        [quantidade]: value


    });
}


const submitForm = event => {
    event.preventDefault();

    props.updateUser(product.id, product);
};



/*
useEffect(() => {
    setProduct(props.currentUser);
}, [props]);
*/

    return (
        <div className="row">

            <form onSubmit={submitForm} className="col s12"
                >
                <div className="row">
                    <div className="input-field col s12">

                        <input type="text" 
                            id={product.id} 
                            name="name"
                            value={product.nome}
                            onChange={handleInputChange} 
                            required 
                            />
                        <label htmlFor="name"></label>
                    </div>
                </div>

                <div className="row">
                    <div className="input-field col s12">

                        <input 
                            type="text" 
                            name="username" 
                            value={product.descricao}
                            onChange={handleInputChange} 
                            required 
                            />
                        <label htmlFor="username"></label>
                    </div>
                </div>

                <div className="row">
                    <div className="input-field col s12">

                        <input 
                            type="text" 
                            name="username" 
                            value={product.modelo}
                            onChange={handleInputChange} 
                            required 
                            />
                        <label htmlFor="username"></label>
                    </div>
                </div>

                <div className="row">
                    <div className="input-field col s12">

                        <input 
                            type="text" 
                            name="username" 
                            value={product.marca}
                            onChange={handleInputChange} 
                            required 
                            />
                        <label htmlFor="username"></label>
                    </div>
                </div>

                <div className="row">
                    <div className="input-field col s12">

                        <input 
                            type="text" 
                            name="username" 
                            value={product.tamanho}
                            onChange={handleInputChange} 
                            required 
                            />
                        <label htmlFor="username"></label>
                    </div>
                </div>

                <div className="row">
                    <div className="input-field col s12">

                        <input 
                            type="text" 
                            name="username" 
                            value={product.valor}
                            onChange={handleInputChange} 
                            required 
                            />
                        <label htmlFor="username"></label>
                    </div>
                </div>

                <div className="row">
                    <div className="input-field col s12">

                        <input 
                            type="text" 
                            name="username" 
                            value={product.quantidade}
                            onChange={handleInputChange} 
                            required 
                            />
                        <label htmlFor="username"></label>
                    </div>
                </div>


                <div className="row">
                    <div className="input-field col s12 m6">

                        <button className="waves-effect waves-light btn">Update</button>
                    </div>

                    <div className="input-field col s12 m6">

                        <button 
                            className="waves-effect waves-light btn"
                            onClick={() => props.setEditing(false)}
                            >Cancel</button>
                    </div>
                </div>
            </form>
        </div>
    );
};

export default EditUserForm;
