import React, { useState, Fragment, useEffect } from 'react';
import EditUserForm from './Componentes/form/edit';
import UserTable from './Componentes/Table/index';

import api from '../services/api';

const App = () => {
    // Data

    const [product, setProduct]= useState([]);


        this.state = {
            users: [],
            currentUser: { id: null, nome: '', descricao: '', modelo: '', marca: '', tamanho: '', valor: '', quantidade: '' },
            editing: false
        }





    useEffect(()=>{


        api.get('product').then(response =>{
          setProduct(response.data);

      })

      }, [product]);






	const updateUser = (id, data) => {
        api.put(`product/${id}`, data)
            .then(res => {

                this.useEffect();
            });


            this.setState({ 
                currentUser: { id: null, name: '', username: '' }
            });

        setEditing(false);


    }


	const editRow = user => {
		setEditing(true)

		setCurrentUser({ id: user.id, nome: user.nome, descricao: user.descricao, modelo: user.modelo, marca: user.marca, tamanho: user.tamanho, valor: user.valor, quantidade: user.quantidade })
    }

    setEditing = isEditing => {

        this.setState({ editing: isEditing });
    };


	return (
		<div className="container">
			<h1>CRUD App with Hooks</h1>
			<div className="flex-row">
				<div className="flex-large">
					{editing ? (
						<Fragment>
							<h2>Edit user</h2>
							<EditUserForm
								editing={editing}
								setEditing={setEditing}
								currentUser={currentUser}
								updateUser={updateUser}
							/>
						</Fragment>
					) : (
						<Fragment>
							<h2>Add user</h2>

						</Fragment>
					)}
				</div>
				<div className="flex-large">
					<h2>View users</h2>
					<UserTable product={product} editRow={editRow} deleteUser={deleteUser} />
				</div>
			</div>
		</div>
	)
}

export default App
