import React, { useEffect, useState, Component, Fragment } from "react";
import { Link, useHistory } from "react-router-dom";
//import {FiPower, FiTrash2 } from 'react-icons/fi';

import "bootstrap/dist/css/bootstrap.min.css";
import Table from "react-bootstrap/Table";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";

import api from "../../services/api";
import Tablet from "./table";
import EditUserForm from './edit';

export default function Profile() {
  const [product, setProduct] = useState([]);
  const [nome, setNome] = useState("");
  const [id, setId] = useState("");
  const [descricao, setDescricao] = useState("");
  const [modelo, setModelo] = useState("");
  const [marca, setMarca] = useState("");
  const [tamanho, setTamanho] = useState("");
  const [valor, setValor] = useState("");
  const [quantidade, setQuantidade] = useState("");

  const inicial = { id: null, nome: '', descricao: '', modelo: '', marca: '', tamanho: '', valor: '', quantidade: ''};



  const [ currentUser, setCurrentUser ] = useState(inicial);
const [ editing, setEditing ] = useState(false);

  useEffect(() => {
    api.get("product").then((response) => {
      setProduct(response.data);
    });
  }, [product]);

  async function handleDeleteIncident(id) {
    try {
      await api.delete(`product/${id}`);

      setProduct(product.filter((pro) => product.id != id));
    } catch (err) {
      alert("Erro ao deletar caso, tente novamente.");
    }
  }

  async function handleNewIncident(e) {
    e.preventDefault();
    const data = {
      nome,
      descricao,
      modelo,
      marca,
      tamanho,
      valor,
      quantidade,
    };
    try {
      await api.post("product", data);
    } catch (err) {
      alert("Erro ao cadastrar Produtos, tente novamente.");
    }
  }



  async function updateUser(e) {

    e.preventDefault();
    const data = {
      id, 
      nome,
      descricao,
      modelo,
      marca,
      tamanho,
      valor,
      quantidade
    }

    try {
      await api.put(`product/${id}`, data);
    } catch (err) {
      alert("Erro ao cadastrar Produtos, tente novamente.");
    }
  }




  const handleInputChange = e =>{
  const {nome, descricao, modelo, marca, tamanho, valor, quantidade, value} = e.target;
   setProduct({

       [nome]: value,
       [descricao]: value,
       [modelo]: value,
       [marca]: value,
       [tamanho]: value,
       [valor]: value,
       [quantidade]: value


   });
}


  const editRow = user => {
		setEditing(true)

		setCurrentUser({ id: user.id, nome: user.nome, descricao: user.descricao, modelo: user.modelo, marca: user.marca, tamanho: user.tamanho, valor: user.valor, quantidade: user.quantidade })
    }
/*

    const submitForm = event => {
      event.preventDefault();

      updateUser(product.id, product);
  };


<EditUserForm 
                                    editing={editing}
                                    setEditing={setEditing}
                                    currentUser={currentUser}
                                    updateUser={updateUser} 
                                />


  */   




  return (
    <React.Fragment>
      <div className="container">
        <h1>CRUD App with Hooks</h1>
        <div className="flex-row">
          <div className="flex-large">
            {editing ? (
              <Fragment>
                <h2>Edit user</h2>

                <div className="row">

<form  className="col s12"
    >
    <div className="row">
        <div className="input-field col s12">

            <input type="text" 
                id={product.id} 
                name="name"
                value={product.nome}
                onChange={handleInputChange} 
                required 
                />
            <label htmlFor="name"></label>
        </div>
    </div>

    <div className="row">
        <div className="input-field col s12">

            <input 
                type="text" 
                name="username" 
                value={product.descricao}
                onChange={handleInputChange} 
                required 
                />
            <label htmlFor="username"></label>
        </div>
    </div>

    <div className="row">
        <div className="input-field col s12">

            <input 
                type="text" 
                name="username" 
                value={product.modelo}
                onChange={handleInputChange} 
                required 
                />
            <label htmlFor="username"></label>
        </div>
    </div>

    <div className="row">
        <div className="input-field col s12">

            <input 
                type="text" 
                name="username" 
                value={product.marca}
                onChange={handleInputChange} 
                required 
                />
            <label htmlFor="username"></label>
        </div>
    </div>

    <div className="row">
        <div className="input-field col s12">

            <input 
                type="text" 
                name="username" 
                value={product.tamanho}
                onChange={handleInputChange} 
                required 
                />
            <label htmlFor="username"></label>
        </div>
    </div>

    <div className="row">
        <div className="input-field col s12">

            <input 
