import React, { Component } from 'react';
import qs from 'querystring';

import api from '../../services/api';

import UserTable from '../table/UserTable';
import EditUserForm from '../forms/EditUserForm';

class Home extends Component {

    constructor(props) {
        super(props);

        this.state = {
            product: [],
            currentUser: { id: null, nome: '', descricao: '' },
            editing: false
        }
    }

    componentDidMount() {
        this.refreshUserTable();
    }


    efreshUserTable(){

        api.get('product').then(response =>{
          setProduct(response.data);

      })
    }


    addUser = pro => {

        api.post('api', qs.stringify(pro))
            .then(res => {
                this.refreshUserTable();
            });
    };

    deleteUser = id => {

        api.delete(`api/${id}`)
            .then(res => {
                this.refreshUserTable();
            });
    };

    updateUser = (id, pro) => {

        api.put(`api/${id}`, qs.stringify(pro))
            .then(res => {

                this.refreshUserTable();
            });

        this.setState({ 
            currentUser: { id: null, nome: '', descricao: '' }
        });

        this.setEditing(false);
    };

    editRow = pro => {

        this.setState({ 
            currentUser: { id: pro.id, nome: pro.nome, descricao: pro.descricao }
        });

        this.setEditing(true);
    };

    setEditing = isEditing => {

        this.setState({ editing: isEditing });
    };

    render () {
        const { product } = this.state;

        return (
            <div className="container">

                <div className="row">

                    {
                        this.state.editing ? (
                            <div className="col s12 l6">
                                <h4>Edit User</h4>
                                <EditUserForm 
                                    editing={this.state.editing}
                                    setEditing={this.setEditing}
                                    currentUser={this.state.currentUser}
                                    updateUser={this.updateUser} 
                                />
                            </div>
                        ) : (
                            <div className="col s12 l6">
                                <h4>Add user</h4>

                            </div>
                        )
                    }

                    <div className="col s12 l6">
                        <h5>product</h5>
                        <UserTable product={product} editRow={this.editRow} deleteUser={this.deleteUser} />
                    </div>
                </div>
            </div>
        );
    };
};

export default Home;
