<td>{pro.quantidade}</td>
<td><Button  /*onClick={()=> updateUser(pro.id)}*/ variant="warning">Edit</Button>{' '}</td>
<td><Button onClick={()=> handleDeleteIncident(pro.id)} variant="danger">X</Button>{' '}</td>

