import React, { useState, Fragment, useEffect, Component } from 'react';
import EditUserForm from './Componentes/form/edit';
import UserTable from './Componentes/Table/index';

import api from '../services/api';

class App_ extends Component {

    constructor(props) {
        super(props);

        this.state = {
            product: [],
            currentUser: { id: null, nome: '', descricao: '', modelo: '', marca: '', tamanho: '', valor: '', quantidade: '' },
            editing: false
        }
    }

    componentDidMount() {
        this.refreshUserTable();
    }

    refreshUserTable() {
        this.usersData = api.get('product')
            .then(response => response.data)
            .then(data => {

                this.setState({ 
                    users: data.data,
                    setUsers: data.data
                });
            });
    }




    updateUser = (id, data) => {

        api.put(`product/${id}`, data)
            .then(res => {

                this.refreshUserTable();
            });

        this.setState({ 
            currentUser: { id: null, nome: '', descricao: '', modelo: '', marca: '', tamanho: '', valor: '', quantidade: '' }
        });

        this.setEditing(false);
    };

    editRow = user => {

        this.setState({ 
            currentUser: { id: user.id, nome: user.nome, descricao: user.descricao, modelo: user.modelo, marca: user.marca, tamanho: user.tamanho, valor: user.valor, quantidade: user.quantidade }
        });

        this.setEditing(true);
    };

    setEditing = isEditing => {

        this.setState({ editing: isEditing });
    };

    render () {
        const { users } = this.state;

        return (
            <div className="container">

                <div className="row">

                    {
                        this.state.editing ? (
                            <div className="col s12 l6">
                                <h4>Edit User</h4>
                                <EditUserForm 
                                    editing={this.state.editing}
                                    setEditing={this.setEditing}
                                    currentUser={this.state.currentUser}
                                    updateUser={this.updateUser} 
                                />
                            </div>
                        ) : (
                            <div className="col s12 l6">
                                <h4>Add user</h4>

                            </div>
                        )
                    }

                    <div className="col s12 l6">
                        <h5>Users</h5>
                        <UserTable users={users} editRow={this.editRow} deleteUser={this.deleteUser} />
                    </div>
                </div>
            </div>
        );
    };
};

export default App_;
