import React, { useState, useEffect } from "react";

import "bootstrap/dist/css/bootstrap.min.css";
import Table from "react-bootstrap/Table";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import api from "../../services/api";
import index from "../index";

const EditUserForm = (props) => {

  async function updateUser(e) {
    e.preventDefault();
    const data = {
      id: id,
      nome: nome,
      descricao: descricao,
      modelo: modelo,
      marca: marca,
      tamanho: tamanho,
      valor: valor,
      quantidade: quantidade,
    };
    try {
      await api.put(`product/${id}`, data);
      alert("Editado");
    } catch (err) {
      alert("Erro ao cadastrar Produtos, tente novamente.");
    }
  }


 const  enviar =(e)=>{

  }

  return (
    <Form onSubmit={updateUser}>
      <Form.Group>
        <Form.Control
          value={nome}
          onChange={(e) => setNome(e.target.value)}
          type="text"
          placeholder="Nome do produto"
        />
      </Form.Group>

      <Form.Group>
        <Form.Control
          value={descricao}
          onChange={(e) => setDescricao(e.target.value)}
          type="text"
          placeholder="Descrição do produto"
        />
      </Form.Group>

      <Form.Group>
        <Form.Control
          value={modelo}
          onChange={(e) => setModelo(e.target.value)}
          type="text"
          placeholder="Modelo do produto"
        />
      </Form.Group>

      <Form.Group>
        <Form.Control
          value={marca}
          onChange={(e) => setMarca(e.target.value)}
          type="text"
          placeholder="Marca do produto"
        />
      </Form.Group>

      <Form.Group>
        <Form.Control
          value={tamanho}
          onChange={(e) => setTamanho(e.target.value)}
          type="number"
          placeholder="Dimensão do produto"
        />
      </Form.Group>

      <Form.Group>
        <Form.Control
          value={valor}
          onChange={(e) => setValor(e.target.value)}
          type="number"
          placeholder="Valor do produto"
        />
      </Form.Group>

      <Form.Group>
        <Form.Control
          value={quantidade}
          onChange={(e) => setQuantidade(e.target.value)}
          type="number"
          placeholder="Quantidade desse produto"
        />
      </Form.Group>

      <Button variant="primary" type="submit">
        update
      </Button>

      <Button className="waves-effect waves-light btn">Cancel</Button>
    </Form>
  );
};

export default EditUserForm;
