import React, { useEffect, useState, Component, Fragment } from "react";
import { Link, useHistory } from "react-router-dom";
//import {FiPower, FiTrash2 } from 'react-icons/fi';

import "bootstrap/dist/css/bootstrap.min.css";
import Table from "react-bootstrap/Table";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";

import api from "../services/api";
import Tablet from "./Lista";
import Adicionar from "./Adicionar/index";

import EditUserForm from "./Editar";

export default function Profile() {
  const [product, setProduct] = useState([]);

  const [id, setId] = useState(props.currentUser.id);
  const [nome, setNome] = useState(props.currentUser.nome);
  const [descricao, setDescricao] = useState(props.currentUser.descricao);
  const [modelo, setModelo] = useState(props.currentUser.modelo);
  const [marca, setMarca] = useState(props.currentUser.marca);
  const [tamanho, setTamanho] = useState(props.currentUser.tamanho);
  const [valor, setValor] = useState(props.currentUser.valor);
  const [quantidade, setQuantidade] = useState(props.currentUser.quantidade);

  useEffect(() => {
    api.get("product").then((response) => {
      setProduct(response.data);
    });
  }, [product]);

  async function handleDeleteIncident(id) {
    try {
      await api.delete(`product/${id}`);

      setProduct(product.filter((pro) => product.id != id));
    } catch (err) {
      alert("Erro ao deletar caso, tente novamente.");
    }
  }

  const inicial = {
    id: null,
    nome: "",
    descricao: "",
    modelo: "",
    marca: "",
    tamanho: "",
    valor: "",
    quantidade: "",
  };

  const [currentUser, setCurrentUser] = useState(inicial);
  const [editing, setEditing] = useState(false);

  const editRow = (user) => {
    setEditing(true);

    setCurrentUser({
      id: user.id,
      nome: user.nome,
      descricao: user.descricao,
      modelo: user.modelo,
      marca: user.marca,
      tamanho: user.tamanho,
      valor: user.valor,
      quantidade: user.quantidade,
    });
  };



  return (
    <React.Fragment>
      <div className="container">
        <h1>Produto</h1>
        <div className="flex-row">
          <div className="flex-large">
            {editing ? (
              <Fragment>
                <h2>Editar</h2>

                <EditUserForm
                  editing={editing}
                  setEditing={setEditing}
                  currentUser={currentUser}

                />
              </Fragment>
            ) : (
              <Adicionar />
            )}
          </div>

          <div className="flex-large">
            <h2>Lista</h2>

            <Tablet
              product={product}
              handleDeleteIncident={handleDeleteIncident}
              editRow={editRow}
            />
          </div>
        </div>
      </div>
    </React.Fragment>
  );
}
