import React, { useState, useEffect } from 'react';


import "bootstrap/dist/css/bootstrap.min.css";
import Table from "react-bootstrap/Table";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";

const EditUserForm = props => {
    const [product, setProduct] = useState(props.currentUser);


   const handleInputChange = e =>{
   const {id, nome, descricao, modelo, marca, tamanho, valor, quantidade, value} = e.target;

   setProduct({ [nome]: value });


}


const submitForm = event => {
    event.preventDefault();

    props.updateUser(event);
};





/*
useEffect(() => {
    setProduct(props.currentUser);
}, [props]);
*/

    return (
        <Form onSubmit={submitForm}>


                <Form.Group>
                    <Form.Label>Producto</Form.Label>



                    <Form.Control
                      value={product.id}

                      onChange={handleInputChange}

                      type="text"
                      placeholder="id do produto"
                    />
                  </Form.Group>


                  <Form.Group>
                    <Form.Label>Producto</Form.Label>



                    <Form.Control
                      value={product.nome}
                      onChange={handleInputChange}
                      type="text"
                      placeholder="Nome do produto"
                    />
                  </Form.Group>

                  <Form.Group>
                    <Form.Control
                      value={product.descricao}
                      onChange={handleInputChange}
                      type="text"
                      placeholder="Descrição do produto"
                    />
                  </Form.Group>

                  <Form.Group>
                    <Form.Control
                      value={product.modelo}
                      onChange={handleInputChange}
                      type="text"
                      placeholder="Modelo do produto"
                    />
                  </Form.Group>

                  <Form.Group>
                    <Form.Control
                      value={product.marca}
                      onChange={handleInputChange}
                      type="text"
                      placeholder="Marca do produto"
                    />
                  </Form.Group>

                  <Form.Group>
                    <Form.Control
                      value={product.tamanho}
                      onChange={handleInputChange}
                      type="number"
                      placeholder="Dimensão do produto"
                    />
                  </Form.Group>

                  <Form.Group>
                    <Form.Control
                      value={product.valor}
                      onChange={handleInputChange}
                      type="number"
                      placeholder="Valor do produto"
                    />
                  </Form.Group>

                  <Form.Group>
                    <Form.Control
                      value={product.quantidade}
                      onChange={handleInputChange}
                      type="number"
                      placeholder="Quantidade desse produto"
                    />
                  </Form.Group>

                  <Button variant="primary" type="submit">
                    update
                  </Button>



                        <Button 
                            className="waves-effect waves-light btn"

                            >Cancel</Button>
                </Form>
    );
};

export default EditUserForm;
