import React, { useEffect, useState, Component, Fragment } from "react";
import { Link, useHistory } from "react-router-dom";
//import {FiPower, FiTrash2 } from 'react-icons/fi';

import "bootstrap/dist/css/bootstrap.min.css";
import Table from "react-bootstrap/Table";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";

import api from "../services/api";
import Tablet from "./Lista/table";
import Adicionar from './Adicionar/index';

import EditUserForm from './Editar';

export default function Profile() {
  const [product, setProduct] = useState([]);



  const inicial = { id: null, nome: '', descricao: '', modelo: '', marca: '', tamanho: '', valor: '', quantidade: ''};



  const [ currentUser, setCurrentUser ] = useState(inicial);
const [ editing, setEditing ] = useState(false);

  useEffect(() => {
    api.get("product").then((response) => {
      setProduct(response.data);
    });
  }, [product]);

  async function handleDeleteIncident(id) {
    try {
      await api.delete(`product/${id}`);

      setProduct(product.filter((pro) => product.id != id));
    } catch (err) {
      alert("Erro ao deletar caso, tente novamente.");
    }
  }










  const handleInputChange = e =>{
  const {nome, descricao, modelo, marca, tamanho, valor, quantidade, value} = e.target;
   setProduct({

       [nome]: value,
       [descricao]: value,
       [modelo]: value,
       [marca]: value,
       [tamanho]: value,
       [valor]: value,
       [quantidade]: value


   });
}


  const editRow = user => {
		setEditing(true)

		setCurrentUser({ id: user.id, nome: user.nome, descricao: user.descricao, modelo: user.modelo, marca: user.marca, tamanho: user.tamanho, valor: user.valor, quantidade: user.quantidade })
    }
/*

    const submitForm = event => {
      event.preventDefault();

      updateUser(product.id, product);
  };


<EditUserForm 
                                    editing={editing}
                                    setEditing={setEditing}
                                    currentUser={currentUser}
                                    updateUser={updateUser} 
                                />


  */   




  return (
    <React.Fragment>
      <div className="container">
        <h1>CRUD App with Hooks</h1>
        <div className="flex-row">
          <div className="flex-large">
            {editing ? (
              <Fragment>
                <h2>Edit user</h2>


                <EditUserForm

                                    editing={editing}
                                    setEditing={setEditing}
                                    currentUser={currentUser}
                                    updateUser={updateUser}


              />

              </Fragment>
            ) : (

              <Adicionar />


            )}


          </div>

          <div className="flex-large">
            <h2>View users</h2>

            <Tablet
              product={product}
              handleDeleteIncident={handleDeleteIncident}
              editRow={editRow}
            />
          </div>
        </div>
      </div>






    </React.Fragment>
  );
}
