import React, { useState, useEffect } from 'react';

const EditUserForm = props => {
    const [product, setProduct] = useState(props.currentUser);

   const handleInputChange = e =>{
   const {nome, descricao, modelo, marca, tamanho, valor, quantidade, value} = e.target;
    setProduct({

        [nome]: value,
        [descricao]: value,
        [modelo]: value,
        [marca]: value,
        [tamanho]: value,
        [valor]: value,
        [quantidade]: value


    });
}


const submitForm = event => {
    event.preventDefault();

    props.updateUser(product.id, product);
};





/*
useEffect(() => {
    setProduct(props.currentUser);
}, [props]);
*/

    return (
        <Form onSubmit={updateUser}  >


                <Form.Group>
                    <Form.Label>Producto</Form.Label>



                    <Form.Control
                      value={product.id}

                      onChange={handleInputChange}

                      type="number"
                      placeholder="id do produto"
                    />
                  </Form.Group>


                  <Form.Group>
                    <Form.Label>Producto</Form.Label>



                    <Form.Control
                      value={nome}
                      onChange={(e) => setNome(e.target.value)}
                      type="text"
                      placeholder="Nome do produto"
                    />
                  </Form.Group>

                  <Form.Group>
                    <Form.Control
                      value={descricao}
                      onChange={(e) => setDescricao(e.target.value)}
                      type="text"
                      placeholder="Descrição do produto"
                    />
                  </Form.Group>

                  <Form.Group>
                    <Form.Control
                      value={modelo}
                      onChange={(e) => setModelo(e.target.value)}
                      type="text"
                      placeholder="Modelo do produto"
                    />
                  </Form.Group>

                  <Form.Group>
                    <Form.Control
                      value={marca}
                      onChange={(e) => setMarca(e.target.value)}
                      type="text"
                      placeholder="Marca do produto"
                    />
                  </Form.Group>

                  <Form.Group>
                    <Form.Control
                      value={tamanho}
                      onChange={(e) => setTamanho(e.target.value)}
                      type="number"
                      placeholder="Dimensão do produto"
                    />
                  </Form.Group>

                  <Form.Group>
                    <Form.Control
                      value={valor}
                      onChange={(e) => setValor(e.target.value)}
                      type="number"
                      placeholder="Valor do produto"
                    />
                  </Form.Group>

                  <Form.Group>
                    <Form.Control
                      value={quantidade}
                      onChange={(e) => setQuantidade(e.target.value)}
                      type="number"
                      placeholder="Quantidade desse produto"
                    />
                  </Form.Group>

                  <Button variant="primary" type="submit">
                    update
                  </Button>



                        <Button 
                            className="waves-effect waves-light btn"
                            onClick={() => setEditing(false)}
                            >Cancel</Button>
                </Form>
    );
};

export default EditUserForm;
