import React, { useEffect, useState, Component }from 'react';
import { Link, useHistory } from 'react-router-dom';
//import {FiPower, FiTrash2 } from 'react-icons/fi';




import 'bootstrap/dist/css/bootstrap.min.css';
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

import api from '../../services/api';

export default function table(props){


  async function handleDeleteIncident(id) {
    try {
      await api.delete(`product/${id}`);

      setProduct(product.filter((pro) => product.id != id));
    } catch (err) {
      alert("Erro ao deletar caso, tente novamente.");
    }
  }


return(

    <Table striped bordered hover>
<thead>
  <tr>
    <th>ID</th>
    <th>NOME</th>
    <th>DESCRIÇÃO</th>
    <th>MODELO</th>
    <th>MARCA</th>
    <th>TAMANHO</th>
    <th>VALOR</th>
    <th>QUANTIDADE</th>
    <th>AÇÃO: I </th>
    <th>AÇÃO: II</th>
  </tr>
</thead>
{props.product.map(pro => (
<tbody>



  <tr>
<td>{pro.id}</td>
<td>{pro.nome}</td>
<td>{pro.descricao}</td>
<td>{pro.modelo}</td>
<td>{pro.marca}</td>
<td>{pro.tamanho}</td>
<td>{pro.valor}</td>
<td>{pro.quantidade}</td>
<td><Button  onClick={() => props.editRow(pro)} variant="warning">Edit</Button>{' '}</td>
<td><Button onClick={()=> props.handleDeleteIncident(pro.id)} variant="danger">X</Button>{' '}</td>

  </tr>





</tbody>
))}
</Table>
)


}
