import React, { useEffect, useState, Component, Fragment } from "react";
import { Link, useHistory } from "react-router-dom";
//import {FiPower, FiTrash2 } from 'react-icons/fi';

import "bootstrap/dist/css/bootstrap.min.css";
import Table from "react-bootstrap/Table";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";

import api from "../services/api";
import Tablet from "./Lista";
import Adicionar from "./Adicionar/index";

import EditUserForm from "./Editar";

export default function Profile() {
  const [product, setProduct] = useState([]);

  useEffect(() => {
    api.get("product").then((response) => {
      setProduct(response.data);
    });
  }, [product]);

  async function deletarProduto(id) {
    try {
      await api.delete(`product/${id}`);

      setProduct(product.filter((pro) => product.id != id));
      alert("Excluido");

    } catch (err) {
      alert("Erro ao deletar caso, tente novamente.");
    }
  }



  const [estadoAtual, setEstadoAtual] = useState({    id: null,
    nome: "",
    descricao: "",
    modelo: "",
    marca: "",
    tamanho: "",
    valor: "",
    quantidade: "",});

  const [editing, setEditing] = useState(false);

  const editRow = (user) => {
    setEditing(true);

    setEstadoAtual({
      id: user.id,
      nome: user.nome,
      descricao: user.descricao,
      modelo: user.modelo,
      marca: user.marca,
      tamanho: user.tamanho,
      valor: user.valor,
      quantidade: user.quantidade,
    });
  };



  return (
    <React.Fragment>
      <div className="container">
        <h1>Produto</h1>
        <div className="flex-row">
          <div className="flex-large">
            {editing ? (
              <Fragment>
                <h2>Editar</h2>

                <EditUserForm
                  editing={editing}
                  setEditing={setEditing}
                  currentUser={currentUser}

                />
              </Fragment>
            ) : (

            <Fragment>
              <h2>Adicionar</h2>
              <Adicionar
                        editing={editing}
                        setEditing={setEditing}
                       currentUser={currentUser}        
               />
              </Fragment>
            )}
          </div>

          <div className="flex-large">
            <h2>Lista</h2>

            <Tablet
              product={product}
              deletarProduto={deletarProduto}
              editRow={editRow}
            />
          </div>
        </div>
      </div>
    </React.Fragment>
  );
}
