import React, { useEffect, useState }from 'react';
import { Link, useHistory } from 'react-router-dom';
//import {FiPower, FiTrash2 } from 'react-icons/fi';

import 'bootstrap/dist/css/bootstrap.min.css';
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import FormControl from 'react-bootstrap/FormControl';
import api from '../../services/api';

export default function Profile(){


  const [product, setProduct]= useState([]);
  const [nome, setNome] = useState('');
  const [descricao, setDescricao] = useState('');
  const [modelo, setModelo] = useState('');
  const [marca, setMarca] = useState('');
  const [tamanho, setTamanho] = useState('');
  const [valor, setValor] = useState('');
  const [quantidade, setQuantidade] = useState('');

  useEffect(()=>{


  api.get('product').then(response =>{
    setProduct(response.data);
})

}, [product]);


async function handleDeleteIncident(id) {
  try{
      await api.delete(`product/${id}`,);

      setProduct(product.filter( pro => product.id != id ));
  }catch(err){
      alert('Erro ao deletar caso, tente novamente.');
  }
} 


async function handleNewIncident(e){
  e.preventDefault();

  const form = e.currentTarget;
  const [validated, setValidated] = useState(false);

  if (form.checkValidity() === false) {
    e.preventDefault();
    e.stopPropagation();
  }

  setValidated(true);




  const data = {
    nome, 
    descricao,
    modelo,
    marca,
    tamanho,
    valor,
    quantidade
  };





  try{
    await api.post('product', data)
  } catch(err){
    alert('Erro ao cadastrar caso, tente novamente.');
  }


}


  return (

    <React.Fragment>

  <Form onSubmit={handleNewIncident, handleNewIncident } >
  <Form.Group >
    <Form.Label>Producto</Form.Label>
    <Form.Control value={nome}  onChange={e=> setNome(e.target.value)} type="text" placeholder="Nome do produto" />
  </Form.Group>

  <Form.Group >
    <Form.Control value={descricao}  onChange={e=> setDescricao(e.target.value)} type="text" placeholder="Descrição do produto" />
  </Form.Group>

  <Form.Group >
    <Form.Control value={modelo}  onChange={e=> setModelo(e.target.value)} type="text" placeholder="Modelo do produto" />
  </Form.Group>

  <Form.Group >
    <Form.Control value={marca}  onChange={e=> setMarca(e.target.value)} type="text" placeholder="Marca do produto" />
  </Form.Group>

  <Form.Group >
    <Form.Control value={tamanho}  onChange={e=> setTamanho(e.target.value)} type="number" placeholder="Dimensão do produto" />
  </Form.Group>

  <Form.Group >
    <Form.Control value={valor}  onChange={e=> setValor(e.target.value)} type="number" placeholder="Valor do produto" />
  </Form.Group>

  <Form.Group >
    <Form.Control value={quantidade}  onChange={e=> setQuantidade(e.target.value)} type="number" placeholder="Quantidade desse produto" />
  </Form.Group>

  <Button variant="primary" type="submit">
    Add
  </Button>
</Form>





<Table striped bordered hover>
<thead>
  <tr>
    <th>ID</th>
    <th>NOME</th>
    <th>DESCRIÇÃO</th>
    <th>MODELO</th>
    <th>MARCA</th>
    <th>TAMANHO</th>
    <th>VALOR</th>
    <th>QUANTIDADE</th>
    <th>AÇÃO: I </th>
    <th>AÇÃO: II</th>
  </tr>
</thead>
{product.map(pro => (
<tbody>



  <tr>
<td>{pro.id}</td>
<td>{pro.nome}</td>
<td>{pro.descricao}</td>
<td>{pro.modelo}</td>
<td>{pro.marca}</td>
<td>{pro.tamanho}</td>
<td>{pro.valor}</td>
<td>{pro.quantidade}</td>
<td><Button  variant="warning">Edit</Button>{' '}</td>
<td><Button onClick={()=> handleDeleteIncident(pro.id)} variant="danger">X</Button></td>

  </tr>





</tbody>
))}
</Table>
</React.Fragment>
  );
}
