import React, { useState, useEffect } from 'react';


import "bootstrap/dist/css/bootstrap.min.css";
import Table from "react-bootstrap/Table";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import api from "../../services/api";


const EditUserForm = props => {
    const [product, setProduct] = useState(props.currentUser);
/*
    const [id, setId] = useState(props.currentUser.id);
    const [nome, setNome] = useState(props.currentUser.nome);

    const [descricao, setDescricao] = useState(props.currentUser.descricao);
    const [modelo, setModelo] = useState(props.currentUser.modelo);
    const [marca, setMarca] = useState(props.currentUser.marca);
    const [tamanho, setTamanho] = useState(props.currentUser.tamanho);
    const [valor, setValor] = useState(props.currentUser.valor);
    const [quantidade, setQuantidade] = useState(props.currentUser.quantidade);

*/

const submitForm = e => {
    e.preventDefault();

    props.updateUser(e);
};

/*
 async function updateUser(e) {

  e.preventDefault();
 const data = {

    id, 
    nome,
    descricao,
    modelo,
    marca,
    tamanho,
    valor,
    quantidade
  }

  try {
    await api.put(`product/${id}`, data);
    alert();
  } catch (err) {
    alert("Erro ao cadastrar Produtos, tente novamente.");
  }
}
*/

    return (
        <Form onSubmit={submitForm}>

                              <Form.Group>
                    <Form.Label>Producto</Form.Label>

                    <Form.Control
                      value={props.id}
                      onChange={(e) => setId(e.target.value)}
                      type="text"
                      placeholder="Nome do produto"
                    />
                  </Form.Group>




                  <Form.Group>
                    <Form.Label>Producto</Form.Label>

                    <Form.Control
                      value={nome}
                      onChange={(e) => setNome(e.target.value)}
                      type="text"
                      placeholder="Nome do produto"
                    />
                  </Form.Group>

                  <Form.Group>
                    <Form.Control
                      value={descricao}
                      onChange={(e) => setDescricao(e.target.value)}
                      type="text"
                      placeholder="Descrição do produto"
                    />
                  </Form.Group>

                  <Form.Group>
                    <Form.Control
                      value={modelo}
                      onChange={(e) => setModelo(e.target.value)}
                      type="text"
                      placeholder="Modelo do produto"
                    />
                  </Form.Group>

                  <Form.Group>
                    <Form.Control
                      value={marca}
                      onChange={(e) => setMarca(e.target.value)}
                      type="text"
                      placeholder="Marca do produto"
                    />
                  </Form.Group>

                  <Form.Group>
                    <Form.Control
                      value={tamanho}
                      onChange={(e) => setTamanho(e.target.value)}
                      type="number"
                      placeholder="Dimensão do produto"
                    />
                  </Form.Group>

                  <Form.Group>
                    <Form.Control
                      value={valor}
                      onChange={(e) => setValor(e.target.value)}
                      type="number"
                      placeholder="Valor do produto"
                    />
                  </Form.Group>

                  <Form.Group>
                    <Form.Control
                      value={quantidade}
                      onChange={(e) => setQuantidade(e.target.value)}
                      type="number"
                      placeholder="Quantidade desse produto"
                    />
                  </Form.Group>

                  <Button variant="primary" type="submit">
                    update
                  </Button>



                        <Button 
                            className="waves-effect waves-light btn"

                            >Cancel</Button>
                </Form>
    );
};

export default EditUserForm;
