import React, { useState, useEffect } from 'react';


import "bootstrap/dist/css/bootstrap.min.css";
import Table from "react-bootstrap/Table";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import api from "../../services/api";
import index from '../Product/index';

const EditUserForm = props => {
    const [product, setProduct] = useState(props.currentUser);
/*
    const [id, setId] = useState(props.currentUser.id);
    const [nome, setNome] = useState(props.currentUser.nome);

    const [descricao, setDescricao] = useState(props.currentUser.descricao);
    const [modelo, setModelo] = useState(props.currentUser.modelo);
    const [marca, setMarca] = useState(props.currentUser.marca);
    const [tamanho, setTamanho] = useState(props.currentUser.tamanho);
    const [valor, setValor] = useState(props.currentUser.valor);
    const [quantidade, setQuantidade] = useState(props.currentUser.quantidade);

*/

const submitForm = e => {
    e.preventDefault();
var v ='passou a infromação';
    props.frase(v);
};


 async function updateUser(e) {

  e.preventDefault();
 const data = {

    id: props.id, 
    nome: props.nome,
    descricao: props.descricao,
    modelo: props.modelo,
    marca: props.marca,
    tamanho: props.tamanho,
    valor: props.valor,
    quantidade: props.quantidade
  }

  try {
    await api.put(`product/${props.id}`, data);
    alert();
  } catch (err) {
    alert("Erro ao cadastrar Produtos, tente novamente.");
  }
}


    return (
        <Form onSubmit={submitForm}>

                              <Form.Group>
                    <Form.Label>Producto</Form.Label>

                    <Form.Control
                      value={props.id}
                      onChange={(e) => props.setId(
                        e.target.value)}
                      type="number"
                      placeholder="Nome do produto"
                    />
                  </Form.Group>




                  <Form.Group>
                    <Form.Label>Producto</Form.Label>

                    <Form.Control
                      value={props.nome}
                      onChange={(e) => e.target.value}
                      type="text"
                      placeholder="Nome do produto"
                    />
                  </Form.Group>

                  <Form.Group>
                    <Form.Control
                      value={props.descricao}
                      onChange={(e) => props.setDescricao(e.target.value)}
                      type="text"
                      placeholder="Descrição do produto"
                    />
                  </Form.Group>

                  <Form.Group>
                    <Form.Control
                      value={props.modelo}
                      onChange={(e) => props.setModelo(e.target.value)}
                      type="text"
                      placeholder="Modelo do produto"
                    />
                  </Form.Group>

                  <Form.Group>
                    <Form.Control
                      value={props.marca}
                      onChange={(e) => props.setMarca(e.target.value)}
                      type="text"
                      placeholder="Marca do produto"
                    />
                  </Form.Group>

                  <Form.Group>
                    <Form.Control
                      value={props.tamanho}
                      onChange={(e) => props.setTamanho(e.target.value)}
                      type="number"
                      placeholder="Dimensão do produto"
                    />
                  </Form.Group>

                  <Form.Group>
                    <Form.Control
                      value={props.valor}
                      onChange={(e) => props.setValor(e.target.value)}
                      type="number"
                      placeholder="Valor do produto"
                    />
                  </Form.Group>

                  <Form.Group>
                    <Form.Control
                      value={props.quantidade}
                      onChange={(e) => props.setQuantidade(e.target.value)}
                      type="number"
                      placeholder="Quantidade desse produto"
                    />
                  </Form.Group>

                  <Button variant="primary" type="submit">
                    update
                  </Button>



                        <Button 
                            className="waves-effect waves-light btn"

                            >Cancel</Button>
                </Form>
    );
};

export default EditUserForm;
