import React, { useState, useEffect } from "react";

import "bootstrap/dist/css/bootstrap.min.css";
import Table from "react-bootstrap/Table";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import api from "../../services/api";
import Col from "react-bootstrap/Col";


export default  function EditUserForm (props){
  const [id, setId] = useState(props.currentUser.id);
  const [nome, setNome] = useState(props.currentUser.nome);
  const [descricao, setDescricao] = useState(props.currentUser.descricao);
  const [modelo, setModelo] = useState(props.currentUser.modelo);
  const [marca, setMarca] = useState(props.currentUser.marca);
  const [tamanho, setTamanho] = useState(props.currentUser.tamanho);
  const [valor, setValor] = useState(props.currentUser.valor);
  const [quantidade, setQuantidade] = useState(props.currentUser.quantidade);
  async function enviarUpdate(e) {
    e.preventDefault();
    props.setEditing(false)
    const data = {
      nome,
      descricao,
      modelo,
      marca,
      tamanho,
      valor,
      quantidade,
    };

    try {
      await api.put(`product/${id}`, data);
      alert("cadastrado");

    } catch (err) {
      alert("Erro ao cadastrar Produtos, tente novamente.");
    }
  }


  return (
    <Form onSubmit={enviarUpdate}>

      <Form.Row>
        <Col>
        <Form.Control
          value={nome}
          onChange={(e) => setNome(e.target.value)}
          type="text"
          placeholder="Nome"
        />
      </Col>

      <Col>

        <Form.Control
          value={descricao}
          onChange={(e) => setDescricao(e.target.value)}
          type="text"
          placeholder="Descrição"
        />

      </Col>
      <Col>

        <Form.Control
          value={modelo}
          onChange={(e) => setModelo(e.target.value)}
          type="text"
          placeholder="Modelo"
        />
     </Col>

     <Col>
        <Form.Control
          value={marca}
          onChange={(e) => setMarca(e.target.value)}
          type="text"
          placeholder="Marca"
        />
      </Col>

      <Col>
        <Form.Control
          value={tamanho}
          onChange={(e) => setTamanho(e.target.value)}
          type="number"
          placeholder="Dimensão"
        />
      </Col>

      <Col>
        <Form.Control
          value={valor}
          onChange={(e) => setValor(e.target.value)}
          type="number"
          placeholder="Valor"
        />
      </Col>

      <Col>
        <Form.Control
          value={quantidade}
          onChange={(e) => setQuantidade(e.target.value)}
          type="number"
          placeholder="Quantidade"
        />
      </Col>
      <Col>
      <Button variant="primary" type="submit">
        ENVIAR
      </Button>
      </Col>
      <Col>
      <Button onClick={() => props.setEditing(false)} className="waves-effect waves-light btn">CANCELAR</Button>
      </Col>
      </Form.Row>
    </Form>




  );
};
