import React, { useEffect, useState, Component }from 'react';
import { Link, useHistory } from 'react-router-dom';
//import {FiPower, FiTrash2 } from 'react-icons/fi';




import 'bootstrap/dist/css/bootstrap.min.css';
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

import api from '../../services/api';
import Tablet from './table';

export default function Profile(){


  const [product, setProduct]= useState([]);
  const [nome, setNome] = useState('');
  const [descricao, setDescricao] = useState('');
  const [modelo, setModelo] = useState('');
  const [marca, setMarca] = useState('');
  const [tamanho, setTamanho] = useState('');
  const [valor, setValor] = useState('');
  const [quantidade, setQuantidade] = useState('');



  useEffect(()=>{


  api.get('product').then(response =>{
    setProduct(response.data);

})

}, [product]);


async function handleDeleteIncident(id) {
  try{
      await api.delete(`product/${id}`,);

      setProduct(product.filter( pro => product.id != id ));
  }catch(err){
      alert('Erro ao deletar caso, tente novamente.');
  }
} 

async function handleNewIncident(e){
  e.preventDefault();
  const data = {
    nome, 
    descricao,
    modelo,
    marca,
    tamanho,
    valor,
    quantidade
  };
  try{
    await api.post('product', data)
  } catch(err){
    alert('Erro ao cadastrar Produtos, tente novamente.');
  }
}


function updateUser(data){  
  api.put(`api/${data.id}`, {

   nome: data.nome, 
   descricao: data.descricao,
   modelo: data.modelo,
   marca: data.marca,
   tamanho: data.tamanho,
   valor: data.valor,
    quantidade: data.quantidade

  }
  )

}


  return (

    <React.Fragment>


<div className="container">
			<h1>CRUD App with Hooks</h1>
			<div className="flex-row">
				<div className="flex-large">
					{editing ? (
						<Fragment>
							<h2>Edit user</h2>
							<EditUserForm
								editing={editing}
								setEditing={setEditing}
								currentUser={currentUser}
								updateUser={updateUser}
							/>
						</Fragment>
					) : (
						<Fragment>
							<h2>Add user</h2>









  <Form onSubmit={handleNewIncident} >
  <Form.Group >
    <Form.Label>Producto</Form.Label>
    <Form.Control value={nome}  onChange={e=> setNome(e.target.value)} type="text" placeholder="Nome do produto" />
  </Form.Group>

  <Form.Group >
    <Form.Control value={descricao}  onChange={e=> setDescricao(e.target.value)} type="text" placeholder="Descrição do produto" />
  </Form.Group>

  <Form.Group >
    <Form.Control value={modelo}  onChange={e=> setModelo(e.target.value)} type="text" placeholder="Modelo do produto" />
  </Form.Group>

  <Form.Group >
    <Form.Control value={marca}  onChange={e=> setMarca(e.target.value)} type="text" placeholder="Marca do produto" />
  </Form.Group>

  <Form.Group >
    <Form.Control value={tamanho}  onChange={e=> setTamanho(e.target.value)} type="number" placeholder="Dimensão do produto" />
  </Form.Group>

  <Form.Group >
    <Form.Control value={valor}  onChange={e=> setValor(e.target.value)} type="number" placeholder="Valor do produto" />
  </Form.Group>

  <Form.Group >
    <Form.Control value={quantidade}  onChange={e=> setQuantidade(e.target.value)} type="number" placeholder="Quantidade desse produto" />
  </Form.Group>

  <Button variant="primary" type="submit">
    Add
  </Button>
</Form>

</Fragment>
					)}
				</div>
				<div className="flex-large">
					<h2>View users</h2>
					<UserTable product={product} editRow={editRow} />
				</div>
			</div>
		</div>



<Tablet
product={product}
handleDeleteIncident={handleDeleteIncident}
editRow={editRow}
/>



</React.Fragment>
  );
}
