            fetch(api)
              .then(res => res.json())
              .then(
                (result) => {
                  this.setState({
                    product: result
                  });
                },
                (error) => {
                  this.setState({ error });
                }
              )


      return(
        <div>
          <h2>Product List</h2>
          <table>
            <thead>
              <tr>
                <th>#ID</th>
                <th>Product Name</th>
                <th>SKU</th>
                <th>Price</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {product.map(product => (
                <tr key={product.id}>
                  <td>{product.id}</td>
                  <td>{product.product_name}</td>
                  <td>{product.sku}</td>
                  <td>{product.price}</td>

                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )
    }








